import { useState } from 'react';
import axios from 'axios';

const Signup = () => {
  const [form, setForm] = useState({ name: '', email: '', password: '' });
  const [msg, setMsg] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.email.endsWith('@iitkgp.ac.in')) {
      setMsg('Only @iitkgp.ac.in emails are allowed.');
      return;
    }
    try {
      await axios.post('/api/auth/signup', form);
      setMsg('Verification email sent. Check your inbox.');
    } catch (err) {
      setMsg(err.response?.data?.error || 'Signup failed.');
    }
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold">Signup</h2>
      <form onSubmit={handleSubmit} className="flex flex-col gap-2 mt-2">
        <input type="text" placeholder="Name" required onChange={e => setForm({ ...form, name: e.target.value })} />
        <input type="email" placeholder="Email" required onChange={e => setForm({ ...form, email: e.target.value })} />
        <input type="password" placeholder="Password" required onChange={e => setForm({ ...form, password: e.target.value })} />
        <button className="bg-blue-600 text-white py-1 px-2">Signup</button>
        {msg && <p className="text-sm text-gray-700 mt-2">{msg}</p>}
      </form>
    </div>
  );
};

export default Signup;
